import React from 'react';
import './About.css';
import { CSSTransition } from 'react-transition-group';

class About extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      appearText:true
    }
  }

  toggleAppear = () => {
    this.setState({appearText: !this.state.appearText})
  }

  componentWillUnmount(){
    this.setState({appearText: !this.state.appearText})
  }

  componentDidMount(){
      document.getElementById('aboutLink').classList.add('headernav-active');
  }

  render(){
    const appearText = this.state;

    /* Media query to add a background-image when the width of browser is less than 700px */
    function addBackground(mq) {
      if (mq.matches) { // If media query matches
        document.body.style.backgroundColor = "yellow";
      } else {
        document.body.style.backgroundColor = "pink";
      }
    }

    var mq = window.matchMedia('(max-width: 768px)');
    addBackground(mq) // Call listener function at run time
    mq.addListener(addBackground) // Attach listener function on state changes


    return (
      <div className="about" style = {{ backgroundColor: this.props.color}}>

        <CSSTransition
          in={appearText}
          appear={true}
          key = {this.props.location.key}
          timeout={400}
          classNames="move">
            <div className='about__text'> 
              <h4  className='about__text-small-title'> {this.props.smallTitle} </h4>
              <h2  className='about__text-big-title'>   {this.props.bigTitle} </h2>
              <div  className = 'about__text-paragraph'>
                <p> {this.props.paragraph} </p>
              </div>
            </div>
        </CSSTransition>
        <img src={this.props.picture} className="about__picture"/>
      </div>
    );
  }
}

export default About;
