import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './components/App/App';
import * as serviceWorker from './serviceWorker';

ReactDOM.render(<App />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();





/* Media query to add a background-image when the width of browser is less than 700px */
function addBackground(mq) {
    if (mq.matches) { // If media query matches
        // hide the picture
        document.querySelector('.about__picture').style.display = "none";
        // display it as a background
        document.querySelector('.about__picture').style.backgroundImage = this.props.picture;

    } else {
        document.querySelector('.about__picture').style.display = "block";
    }
  }

  var mq = window.matchMedia('(max-width: 768px)');
  addBackground(mq) // Call listener function at run time
  mq.addListener(addBackground) // Attach listener function on state changes
