  }

  render() {
    return (
      <header className="header">
        <div className="logo-container">
          <div className="logo">
            <div className="logo__text">
              <p>Vasilis</p>
              <p>Georgoudis</p>
            </div>
          </div>
        </div>

        <div className="center_navigation">
          <NavLink exact to='/' activeClassName='headernav-active' className = {'center_navigation__about' + this.getActiveClass('/about/bio')} onClick={this.animateAbout}> About </NavLink>
          <NavLink to='/work' activeClassName='headernav-active' className = 'center_navigation__work' onClick={this.animateWork}> Work </NavLink>
        </div>

        <div className="social">
          <a href="https://www.linkedin.com/in/vasilis-georgoudis-69211971/" target="_blank"><img src={linkedin} className="social__linkedin"></img></a>
          <a href="https://github.com/masaka222" target="_blank"> <img src={github} className="social__github"></img></a>
        </div>
      </header>
    );
  }
}

Header = withRouter(Header);

export default Header;
