import React, { Component } from 'react';
import {BrowserRouter, Route, Switch } from 'react-router-dom';
import './App.css';
import Header from '../Header/Header';
import Main from '../Main/Main';
import Main2 from '../Main2/Main2';

import gif from '../Main/arrows.gif';

class App extends Component {

  componentDidMount(){
    var path = this.props.location.pathname;
    if (path.startsWith('/about')){
      console.log('starts with about');
      //document.getElementById('aboutLink').classList.add('headernav-active');
    }
    else{
      console.log('does not start with about');
      //document.getElementById('aboutLink').classList.remove('headernav-active');
    }
    //document.getElementById('aboutLink').classList.add('headernav-active');
    //document.getElementById('workLink').classList.remove('headernav-active');
  }
  render() {
    return (
      <div className="App">
        <BrowserRouter>
        <Header/>
         {/* <img id='arrowGif' src={gif} className='appGif' style={{display: 'none'}}></img>*/}
          <Switch>
            <Route exact path='/' component={Main}/>
            <Route path='/about' component={Main}/> {/* Without this, when you were loading an /about/.. page from the urlbar, it would fail load */}
            <Route path='/work' component={Main2}/>
          </Switch>
        </BrowserRouter>
      </div>
    );
  }
}

export default App;
