import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import './Sidebar.css';

class Sidebar extends Component {

  /* This function gives the class of active when the list item is indeed active*/
  getActiveClass = (path) => {
    return this.props.location.pathname === path ? "active" : " "
  }

  render() {
    function init()
    {
      document.onkeydown = keyPressHandler;
      cleanElement(document.getElementById('nav'));
    }

    // removes all #text elements directly attached to the node.
    function cleanElement(element)
    {		
      if (element == null) return 0;
      var numTextElementsRemoved = 0;
      var node = element.firstChild;
      while (node != null)
      {
        var tmp = node.nextSibling;
        if (node.nodeName == '#text')
        {	
          element.removeChild(node);
          numTextElementsRemoved++;
        }
        node = tmp;
      }
      return numTextElementsRemoved;
    }

    function keyPressHandler(e)
    {
      if (!e) e = window.event;
      var selectedItem = document.getElementById('selectedItem');
      if (e.keyCode == 38)
      {
        // up arrow
        if (selectedItem)
        {
          selectedItem.id = '';
          if (selectedItem.previousSibling) selectedItem.previousSibling.id = 'selectedItem';
          else selectedItem.parentNode.lastChild.id = 'selectedItem';
        }
        else document.getElementById('nav').lastChild.id = 'selectedItem';
      }
      else if (e.keyCode == 40)
      {
        // down arrow
        if (selectedItem)
        {
          selectedItem.id = '';
          if (selectedItem.nextSibling) selectedItem.nextSibling.id = 'selectedItem';
          else selectedItem.parentNode.firstChild.id = 'selectedItem';
        }
        else document.getElementById('nav').firstChild.id = 'selectedItem';
      }
      else if (e.keyCode == 13)
      {
        // enter
        if (selectedItem) window.location.href = selectedItem.firstChild.href;
      }
    }

    window.onload = init;

    return (
      <div className="sidebar">
        <div className="sidebar__navigation">
            <ul id='nav' className="sidebar__navigation-list">
                <li className = {"sidebar__navigation-list-item " + this.getActiveClass("/")}>
                  <Link exact to="/">
                    <div className = "list-number">00</div>
                    <div className = {"list-text "+ this.getActiveClass("/")}>Vasilis Georgoudis</div>
                  </Link>
                </li>
                <li className = {"sidebar__navigation-list-item " + this.getActiveClass("/bio")}>
                  <Link exact to="/bio">
                    <div className = "list-number">01</div>
                    <div className = {"list-text "+ this.getActiveClass("/bio")}>What I do</div>
                  </Link>
                </li>
                <li className = {"sidebar__navigation-list-item " + this.getActiveClass("/skills")}>
                  <Link exact to="/skills">
                    <div className = "list-number">02</div>
                    <div className = {"list-text "+ this.getActiveClass("/skills")}>How I do it</div>
                  </Link>
                </li>
                <li className={"sidebar__navigation-list-item " + this.getActiveClass("/moreskills")}>
                  <Link exact to="/moreskills">
                    <div className = "list-number">03</div>
                    <div className = {"list-text "+ this.getActiveClass("/moreskills")}>More Skills</div>
                  </Link>
                </li>
                <li className={"sidebar__navigation-list-item " + this.getActiveClass("/contact")}>
                  <Link exact to="/contact">
                    <div className = "list-number">04</div>
                    <div className = {"list-text "+ this.getActiveClass("/contact")}>Contact Me</div>
                  </Link>
                </li>


            </ul>
        </div> 
      </div>
    );
  }
}

Sidebar = withRouter(Sidebar);

export default Sidebar;
