import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import './Sidebar.css';

class Sidebar extends Component {

  /* This function gives the class of active when the list item is indeed active*/
  getActiveClass = (path) => {
    return this.props.location.pathname === path ? "active" : " "
  }

  render() {

    var active_id = '1';
    Element = document.getElementById(active_id);
    Element.style.color = "red";

    document.onkeyup = KeyCheck;

    function KeyCheck(e) {
        var old_id = active_id;
        var Key = (window.event) ? event.keyCode : e.keyCode;
        switch (Key) {
            case 40:
                active_id++;
                break;
            case 38:
                active_id--;
                break;
        }
        if (active_id < 1) active_id += 3;
        if (active_id > 3) active_id -= 3;
        Old = document.getElementById(old_id);
        Old.style.color = "black";
        New = document.getElementById(active_id);
        New.style.color = "red";
        document.getElementById('text').innerHTML = active_id;
    };

    return (
      <div className="sidebar">
        <div className="sidebar__navigation">
            <ul className="sidebar__navigation-list">
                <li id="1" className = {"sidebar__navigation-list-item " + this.getActiveClass("/")}>
                  <Link exact to="/">
                    <div className = "list-number">00</div>
                    <div className = {"list-text "+ this.getActiveClass("/")}>Vasilis Georgoudis</div>
                  </Link>
                </li>
                <li id="2" className = {"sidebar__navigation-list-item " + this.getActiveClass("/bio")}>
                  <Link exact to="/bio">
                    <div className = "list-number">01</div>
                    <div className = {"list-text "+ this.getActiveClass("/bio")}>What I do</div>
                  </Link>
                </li>
                <li id="3" className = {"sidebar__navigation-list-item " + this.getActiveClass("/skills")}>
                  <Link exact to="/skills">
                    <div className = "list-number">02</div>
                    <div className = {"list-text "+ this.getActiveClass("/skills")}>How I do it</div>
                  </Link>
                </li>
                <li id="4" className={"sidebar__navigation-list-item " + this.getActiveClass("/moreskills")}>
                  <Link exact to="/moreskills">
                    <div className = "list-number">03</div>
                    <div className = {"list-text "+ this.getActiveClass("/moreskills")}>More Skills</div>
                  </Link>
                </li>
                <li id="5" className={"sidebar__navigation-list-item " + this.getActiveClass("/contact")}>
                  <Link exact to="/contact">
                    <div className = "list-number">04</div>
                    <div className = {"list-text "+ this.getActiveClass("/contact")}>Contact Me</div>
                  </Link>
                </li>


            </ul>
        </div> 
      </div>
    );
  }
}

Sidebar = withRouter(Sidebar);

export default Sidebar;
